mapper.select:
SELECT TAKESEQ, TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD HH24:MI') RECEIPTDATE, AUTONUMBER, CUSTOMER_AUTOKIND, CUSTOMER_NAME, CUSTOMER_TEL, REPAIR_ENUM(TAKESEQ) REPAIRCODE_NAME, FN_CODENAME(POSITION, 'AUTO_ROOM') POSITION_NAME, A.POSITION, ETIME, FN_CODENAME(REPAIRMETHOD, 'AUTO_ME') REPAIRMETHOD_NAME, A.TASKSTAT, FN_CODENAME(TASKSTAT, 'AUTO_PROC') TASKSTAT_NAME, TO_CHAR(TURNOVERTIME, 'YYYY-MM-DD HH24:MI') TURNOVERTIME, REPAIRNOTE, A.NOTE, TURNOVER_NOTE FROM CAR_TAKEREPAIR A, CAR_CUSTOMER B WHERE A.CUSTOMER_ID = B.CUSTOMER_ID AND A.TASKSTAT = 'CB-perfect' AND A.TURNOVER = 'Y'
AND AUTONUMBER LIKE '%' ||#{searchAutoNo}|| '%'
AND CUSTOMER_NAME LIKE '%' ||#{searchCusNm}|| '%'
AND CUSTOMER_TEL LIKE '%' ||#{searchCusTel}|| '%'
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') between #{sdate} and #{edate}
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') <= #{edate}
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') >= #{sdate}
AND SUBSTR(ETIME,1,10) = TO_CHAR(SYSDATE, 'YYYY-MM-DD')

SELECT b.H_CODE, d.NAME h_name,b.CODE,b.NAME,COUNT(*) cnt,TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') RECEIPTDATE FROM CAR_LEAD_REPAIR A, CAR_LEADTIME B, CAR_TAKEREPAIR C, CAR_LEADTIME D WHERE a.REPAIRCODE = b.CODE AND a.TAKESEQ = c.TAKESEQ AND c.TASKSTAT IN('CB-standby', 'CB-perfect') AND b.H_CODE = d.CODE
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') between #{sdate} and #{edate}
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') <= #{edate}
AND TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD') >= #{sdate}
AND SUBSTR(ETIME,1,10) = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
group BY b.H_CODE,D.NAME,b.CODE,B.NAME,d.ORD,b.ORD,c.RECEIPTDATE ORDER BY d.ORD,b.ORD

SELECT b.H_CODE, d.NAME H_NAME, b.CODE, b.NAME NAME, SUM(DECODE (PLANDATE,NULL,1,0)) NORMAL_COUNT, SUM(DECODE (PLANDATE,NULL,0,1)) RESERVE_COUNT, COUNT(*) TOTAL_COUNT FROM CAR_LEAD_REPAIR A, CAR_LEADTIME B, CAR_TAKEREPAIR C, CAR_LEADTIME D WHERE a.REPAIRCODE = b.CODE AND a.TAKESEQ = c.TAKESEQ AND c.TASKSTAT IN('CB-standby','CB-perfect') AND b.H_CODE = d.CODE
AND C.RECEIPTDATE BETWEEN #{sdate} AND #{edate} AND C.RECEIPTDATE >= #{sdate}
group BY b.H_CODE, D.NAME, b.CODE, B.NAME, d.ORD, b.ORD ORDER BY d.ORD, b.ORD
