mapper.select:
SELECT * FROM CAR_CUSTOMER
WHERE CUSTOMER_AUTONO = #{checkcarnum}
AND CUSTOMER_TEL = #{tel}

SELECT TAKESEQ, TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD HH24:MI') RECEIPTDATE, TO_CHAR(PLANDATE, 'YYYY-MM-DD HH24:MI') PLANDATE, AUTONUMBER, CUSTOMER_AUTOKIND, CUSTOMER_NAME, CUSTOMER_TEL, REPAIR_ENUM(TAKESEQ) REPAIRCODE_NAME, TASKSTAT, FN_CODENAME(TASKSTAT,'AUTO_PROC') TASKSTAT_NAME FROM CAR_TAKEREPAIR A, CAR_CUSTOMER B WHERE A.CUSTOMER_ID = B.CUSTOMER_ID AND A.PLANDATE IS NOT NULL
AND A.TASKSTAT = #{taskstat}
AND A.RECEIPTDATE BETWEEN #{sdate} AND #{edate} AND A.RECEIPTDATE >= #{sdate}

mapper.update:
UPDATE CAR_TAKEREPAIR
SET TASKSTAT = 'CB-reserveconfirm'
WHERE TAKESEQ = #{takeseq}

UPDATE CAR_TAKEREPAIR
SET TASKSTAT= 'CB-reserveconfirm'
WHERE TAKESEQ = #{takeseq}
