mapper.select:
SELECT TAKESEQ, TO_CHAR(RECEIPTDATE, 'YYYY-MM-DD HH24:MI') RECEIPTDATE, AUTONUMBER, CUSTOMER_AUTOKIND, CUSTOMER_NAME, CUSTOMER_TEL, REPAIR_ENUM(TAKESEQ) REPAIRCODE_NAME, FN_CODENAME(POSITION, 'AUTO_ROOM') POSITION_NAME, A.POSITION, B.KILRO_TOTAL, A.KILRO_NOW, ESTIME, TO_CHAR(TO_DATE(A.ESTIME,'YYYY-MM-DD HH24:MI:SS'),'HH24:MI') AS ESTIME1, FN_CODENAME(REPAIRMETHOD, 'AUTO_ME') REPAIRMETHOD_NAME, A.TASKSTAT, FN_CODENAME(TASKSTAT, 'AUTO_PROC') TASKSTAT_NAME FROM CAR_TAKEREPAIR A, CAR_CUSTOMER B WHERE A.CUSTOMER_ID = B.CUSTOMER_ID AND A.TASKSTAT = 'CB-repaired'
AND A.POSITION = (SELECT TEAM FROM LETTNGNRLMBER WHERE ESNTL_ID = #{loginid})
AND A.POSITION = #{autoroom}

mapper.update:
UPDATE CAR_TAKEREPAIR
SET
	TASKSTAT = #{update_taskstat}

	WHERE TAKESEQ = #{seq}
