mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID as "uniqId", 'USR01' as "userTy", MBER_ID as "userId", MBER_NM as "userNm", MBER_EMAIL_ADRES as "emailAdres", AREA_NO as "areaNo", USE_YN as "useYn" , MBTLNUM as "moblphonNo", GROUP_ID as "groupId", MBER_STTUS as "Sttus", TEAM as team, SBSCRB_DE as "sbscrbDe"
FROM LETTNGNRLMBER WHERE 1=1
AND MBER_STTUS LIKE #{sbscrbSttus}
AND MBER_ID LIKE '%' ||#{searchKeyword}|| '%'
AND MBER_NM LIKE '%' ||#{searchKeyword}|| '%'
ORDER BY 12 DESC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:

UPDATE LETTNGNRLMBER 
SET 
       PASSWORD   =  #{password}
WHERE  ESNTL_ID  = #{uniqId}
